sys_script_include.script:
var MappingConfigCommon = Class.create();
MappingConfigCommon.prototype = {
	initialize: function (gtCache, oTimer) {
		if (gtCache instanceof global.GlobalToolsCache) {
			this._gtCache = gtCache;
		} else {
			this._gtCache = new global.GlobalToolsCache();
		} // if cache
		this.bTimer = ((oTimer) && ((oTimer === true) || (oTimer instanceof global.GlobalToolsDateTime))) ? true : false;
		if (this.bTimer) {
			if (oTimer === true) {
				this._gtDateTime = new global.GlobalToolsDateTime('gtt');
				this._sTimerPrefix = '';
			} else {
				this._gtDateTime = oTimer;
				this._sTimerPrefix = 'mcmc/';
			} // if true
		} // initialize timer
	},

	getDependentData: function (grMappingCfg, bSourceValue) {
		if (this.bTimer) { this._gtDateTime.startTimer(this._sTimerPrefix + 'getDependentData'); }
		var sDependentField = '';
		var sDependentValue = '';
		var sDependentId = grMappingCfg.getValue('dependent_mapping');
		if (sDependentId) {
			var grDependentMapping = new GlideRecord('x_snc_mapping_cm_mapping_config');
			if (grDependentMapping.get(sDependentId)) {
				sDependentField = grDependentMapping.getValue('choice_field');
				if (bSourceValue) {
					sDependentValue = grDependentMapping.getValue('source_value');
				} else {
					sDependentValue = grDependentMapping.getValue('target_value');
					if (grDependentMapping.getValue('type') == 'choice') {
						var grChoice = new GlideRecord('sys_choice');
						if ((sDependentValue) && (grChoice.get(sDependentValue))) {
							sDependentValue = grChoice.getValue('value');
						} // if target is valid choice
					} // if choice
				} // if source value
			} // if dependent mapping
		} // if dependent id
		if (this.bTimer) { this._gtDateTime.stopTimer(this._sTimerPrefix + 'getDependentData'); }
		return { sDependentField: sDependentField, sDependentValue: sDependentValue };
	}, // function getDependentData

	getTargetFields: function (aAffectedFields) {
		if (this.bTimer) { this._gtDateTime.startTimer(this._sTimerPrefix + 'getTargetFields'); }
		var aFields = [];
		var grDict = new GlideRecord('sys_dictionary');
		grDict.addEncodedQuery('sys_idIN' + aAffectedFields);
		grDict.query();
		while (grDict.next()) {
			aFields.push(grDict.getValue('element'));
		} // while fields
		if (this.bTimer) { this._gtDateTime.stopTimer(this._sTimerPrefix + 'getTargetFields'); }
		return aFields;
	}, // function getTargetFields

	type: 'MappingConfigCommon',
	version: '1.0.3',
	author: 'Tom Hauri',
	email: 'tom@hauri.biz'
};
