sys_ui_action.script:
function selectTargetRecord() {
	// && ((!g_scratchpad.sDependentId) || (g_scratchpad.sDependentValue))
	var sSysId = g_form.getUniqueValue();
	var sTable = g_scratchpad.sTable;
	var sQuery = '';
	if (g_scratchpad.sType == 'choice') {
		var sLangQuery = '';
		if (typeof g_lang != undefined) {
			sLangQuery = '^language=' + g_lang;
		} // if g_lang 
		var sDependentQuery = '';
		if ((g_scratchpad.sDependentId) && (g_scratchpad.sDependentValue)) {
			sDependentQuery = '^dependent_value=' + g_scratchpad.sDependentValue;
		}
		sQuery = 'name=' + sTable + '^element=' + g_scratchpad.sField + sLangQuery + sDependentQuery;
		sTable = 'sys_choice';
	} else {
		sQuery = '';
	} // if choice
	var gModal = new GlideModal('x_snc_mapping_cm_MappingCfgTargetRecord');
	gModal.setWidth(600);
	gModal.setPreference('sysparm_mappingid', sSysId);
	gModal.setPreference('sysparm_targettable', sTable);
	gModal.setPreference('sysparm_targetquery', sQuery);
	gModal.setPreference('sysparm_targetvalueempty', g_scratchpad.sTargetValueEmpty);
	var sSourceValue = g_form.getValue('source_display_value');
	sSourceValue = (sSourceValue) ? sSourceValue + ' (' + g_scratchpad.sSourceValue + ')' : g_scratchpad.sSourceValue;
	if (g_scratchpad.bHasDependentMappings) {
		gModal.setPreference('sysparm_popupmessage_dependent', getMessage('map_select_target_message_dependent_popup'));
	} // if dependent maps
	gModal.setPreference('sysparm_popupmessage', getMessage('map_select_target_message_popup') + ' ' + sSourceValue);
	gModal.setPreference('sysparm_popupmessage_empty', getMessage('map_select_target_message_empty'));
	gModal.setTitle(getMessage('map_select_target_title'));
	gModal.render();
} // function selectTargetRecord
